import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component
{
  state={ 
      value : "",
      todo : [],
  };

  saveTodo = ()=>
  {
    const {value,todo} = this.state;
    todo.push(value);
    this.setState({todo : todo,value : ""})
    console.log(todo)
  }

  handleInput = (e)=>
  {
    const val = e.target.value;
    this.setState({value : val})
  }

  render()
  {
    const {value,todo} = this.state;
    return (
      <div>
        <input value={value} onChange={this.handleInput}/>
        <button onClick={this.saveTodo}>Save</button>
        <ul>
          {
            todo.map((item)=>{
              return (
                <li key={item}>{item}</li>
                )
            })
          }
        </ul>
      </div>
    )
  }
}





export default App;
