import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component
{
  state={ 
      value : "",
      todo : [],
  };

  saveTodo = ()=>
  {
    const {value,todo} = this.state;
    todo.push(value);
    this.setState({todo : todo,value : ""})
    console.log(todo)
  }

  handleInput = (e)=>
  {
    const val = e.target.value;
    this.setState({value : val})
  }

  deleteItem = (e)=>
  {
    const {value,todo} = this.state;
    const a = e.target.key;
    todo.splice(a,1);
    this.setState({todo : todo})
  }

  render()
  {
    const {value,todo} = this.state;
    return (
      <div>
        <input value={value} onChange={this.handleInput}/>
        <button onClick={this.saveTodo}>Save</button>
        <ul>
          {
            todo.map((item,i)=>{
              return (
                <li key={i}>{item}
                <button key={i} onClick={this.deleteItem}>Delete</button></li>
                )
            })
          }
        </ul>
      </div>
    )
  }
}





export default App;
